                        function changelocs(loc) {
                            if (loc === 'BMJ in North America') {
                                localStorage.setItem('bmj', 'us');
                            } else {
                                localStorage.setItem('bmj', 'na');
                            }
                        }
                        jQuery(document).ready(function () {
                            var loc = localStorage.getItem('bmj');
                            if (loc === 'us') {
                                jQuery(".nonus-menu").hide();
                                jQuery(".us-menu").show();
                            } else if (loc === 'na') {
                                jQuery(".us-menu").hide();
                                jQuery(".nonus-menu").show();
                            } else {
                                jQuery(".nonus-menu").show();
                                jQuery(".us-menu").hide();
                            }
                        });
