.fourofour h1, .fourofour h2, .fourofour h3, .fourofour h4, .fourofour h5, .fourofour h6{
font-family: Montserrat;
font-weight: 700;
}
.fourofour h1, .fourofour p.lead {
    background-color: rgba(46, 44, 44, 0.8);
    color: #fff;
    display: table;
    padding: 10px;
}
.fourofour .container.displaybarholder {
    display: none;
}
.fourofour .container.main {
